mapper.select:
select * from NOTICE
AND (notice_target=#{role} or notice_target='ALL')
order by notice_regdate desc limit #{pageStart},#{amount}

select * from CS
AND (cs_writer=#{cs_writer})
order by cs_regdate desc limit #{pageStart},#{amount}

select count(*) from (select * from cs c right join projectinfo p on c.cs_project=p.pro_id where team_num=#{eng_team_num}) as eng
or cs_server=#{cs_writer}
or cs_server='ALL'

		select count(*) from CS
		where cs_writer=#{cs_writer}
		;

		select c.*, p.pro_status, p.pro_startdate, p.pro_enddate
		from customer c
    left join projectinfo p on c.cus_id = p.cus_id;
